let vh = window.innerHeight * 0.01;
document.documentElement.style.setProperty('--vh', `${vh}px`);

window.addEventListener('resize', () => {
  let vh = window.innerHeight * 0.01;
  document.documentElement.style.setProperty('--vh', `${vh}px`);
});

$(window).scroll(function() {
  if ($(this).scrollTop()>150) {
    $('.hero__scroll').css('border','2px solid rgba(213,18,36,0)');
    $('.scroll__action').css('visibility','hidden');
  } else {
    $('.hero__scroll').css('border', '2px solid rgba(213,18,36,0.2)');
    $('.scroll__action').css('visibility','visible');
  }
});

function adjust_header_resizer() {
	"use strict";
	//If there is header sizer, adjust to the header height
		var header_sizer = $(".header__sizer");
		if (header_sizer.length > 0) {
			var header_height = $("header").outerHeight();
			header_sizer.css("min-height", header_height+"px");
	}
}

function action_hamburger() {
  var this_el = $('.navbar__hamburger')
      navbar__menu = $('.navbar__content')
      login__container = $('.header__buttons')

  this_el.on('click', function(e){
		this_el.addClass('clicked');

    if ((this_el.attr('aria-expanded') === 'false')) {
      navbar__menu.addClass('js-open');
      login__container.addClass('js-open');
      this_el.attr('aria-expanded','true');
      $.scrollLock( true );
    } else {
      navbar__menu.removeClass('js-open');
      login__container.removeClass('js-open');
      this_el.attr( 'aria-expanded', 'false');
      $.scrollLock( false );
    }
  })
}

$(document).ready(function () {
  adjust_header_resizer();
	action_hamburger();
})

$(window).resize(function () {
  adjust_header_resizer();
})
